export class MyLogger {
    _log: boolean = true;

    public silence(flag: boolean) {
        this._log = !flag;
    }

    public log(...args: any[]): void {
        if (!this._log) {
            return;
        }

        console.log.call(console, `[INF] [${new Date()}]:`, args.join(' '));
    }

    public error(...args: any[]): void {
        console.log.call(console, `[ERR] [${new Date()}]:`, args.join(' '));
    }
}